import React, { Component } from "react";
import Product from "./Product.js";
import ShoppingCart from "./ShoppingCart.js";
import './style.css';


class HomePage extends Component {
    constructor(props) {
      super(props);
    }

    addToCart = (item) => {
      this.props.addToCartCB(item);
    }

    removeAll = (item) => {
      this.props.removeAllCB(item);
    }

    removeOne = (item) => {
      this.props.removeOneCB(item);
    }

    /** Renders the product list by calling renderProduct() on each item. */
    renderProductList() {
      const items = this.props.list.map(item => {
          return this.renderProduct(item)
      });
      return (
        <div id="product-list">
          {items}
        </div>
      );
    }

    /** Renders a product tile to be displayed in product list. */
    renderProduct(item) {
      return (<Product key={item.name}
                item={item}
                addToCartCB={this.addToCart.bind(this)}
                constants={this.props.constants}/>)
    }

    render() {
      return (
        <div className="app-container">
          {this.renderProductList()}
          <ShoppingCart
            cart={this.props.cart}
            constants={this.props.constants}
            quantities={this.props.quantities}
            removeAllCB={this.removeAll.bind(this)}
            addToCartCB={this.addToCart.bind(this)}
            removeOneCB={this.removeOne.bind(this)}/>
        </div>
      )
    }
}

export default HomePage;
