import React, {Component} from 'react';

class Product extends Component {
    constructor(props) {
      super(props);
      this.state = {
          inCart: "False",
          quantity: 0
      };
    }

    addToCart = (event) => {
      // var currCart = {this.props.cart};
      this.setState({quantity: (this.state.quantity + 1)});
      console.log("add to cart");
      if (this.state.inCart == "False") {
        this.props.addToCartCB(this);
        this.setState({inCart: "True"});
      }
      console.log(this.state.inCart)
      console.log(this.state.quantity)

    }

    render() {
       return (
           <div className="restaurant">
              <h2>{this.props.item.name}</h2>
              <h3>Price: ${this.props.item.price}</h3>
              <button onClick={this.props.addToCartCB(this.props.item)}>add to cart!</button>
          </div>
      );
    }

}

export default Product;


