import React, {Component} from 'react';
import './style.css';

class CartItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
          quantity: 1
      };
      // this.addToCart = this.addToCart.bind(this)
    }

    removeOne = (event) => {
      if (this.props.quantity == 1) {
        this.removeFromCart()
      } else {
        console.log("remove one")
        this.props.removeOneCB(this.props.item)
      }
    }

    removeFromCart = (event) => {
      console.log("remove")
      this.props.removeAllCB(this.props.item)
    }

    addToCart = (event) => {
      console.log("add")
      this.setState({ quantity: this.state.quantity + 1})
      // this.setState({quantity: 0})
      this.props.addToCartCB(this.props.item)
    }

    render() {
       return (
           <div className="cart-item">
              <h2>{this.props.item.name}</h2>
              <h3>{this.props.quantity} x ${this.props.item.price}</h3>
              <h3>Cost: ${(this.state.quantity * this.props.item.price)}</h3>
              <button onClick={this.removeFromCart}>remove</button>
              <button onClick={this.removeOne}>-</button>
              <button onClick={this.addToCart}>+</button>
          </div>
      );
    }

}

export default CartItem;
