import React, { Component } from "react";
import "./App.css";
import Product from "./Product.js";
import ShoppingCart from "./ShoppingCart.js";
import CartItem from "./CartItem.js";
import './style.css';


class ProductList extends Component {
    constructor(props) {
      super(props);
      global.cartObjs = []
      this.state = { cart: []}
    }

  renderList() {
    const items = this.props.list.map(item => {
        return <Product key={item.name} item={item} addToCartCB={this.addToCart.bind(this)}/>
    });
    return items;
  }

  renderCart() {
    const items = this.state.cart.map(item => {
      return <CartItem key={item.name} item={item} removeCB={this.removeItem.bind(this)}/>
    });
    // return <ShoppingCart items={items}/>
    global.cartObjs = items
    return items
  }
          // return <Product key={item.name} item={item} addToCartCB={this.addToCart.bind(this)}/>
  // changeQuantity(delta) {
  //   this.setState((state) => {
  //     return {quantity: state.quantity + delta}
  //   });
  // }

  addToCart(item) {
    console.log("item", item)
    if (this.state.cart.includes(item)) {
      console.log("ITEM ALREADY IN CART")
      var index = this.state.cart.indexOf(item)
      console.log("HIHI",this.state.cart[index])
      console.log("HIHI2",global.cartObjs[index])
      global.cartObjs[index].changeQuantity(1).bind(this)
      // update quantity

    } else {
      console.log("cart in add to cart:", this.state.cart.concat([item]))
      this.setState({cart: this.state.cart.concat([item])})
    }

  }

  removeItem(item) {
    if (this.state.cart.includes(item)) {
      if (this.state.cart.length == 1) {
        this.setState({cart: []})
      } else {
        var index = this.state.cart.indexOf(item)
        var holder = this.state.cart
        holder.splice(index,1)
        this.setState({cart: holder})
      }
    }
  }

  render() {
    return (
      <div className="flex-row">
          <div id="product-list">
            {this.renderList()}
          </div>
          <div id="shopping-cart">
            {this.renderCart()}
          </div>
      </div>
    );
  }
}

export default ProductList;
