import React, {Component} from 'react';
import './style.css';

class CartItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
          quantity: 1
      };
      // this.addToCart = this.addToCart.bind(this)
    }

    removeOne = (event) => {
      if (this.props.quantity == 1) {
        this.removeFromCart()
      } else {
        // console.log("remove one")
        this.props.removeOneCB(this.props.item)
      }
    }

    removeFromCart = (event) => {
      // console.log("remove")
      this.props.removeAllCB(this.props.item)
    }

    addToCart = (event) => {
      // console.log("add")
      this.setState({ quantity: this.state.quantity + 1})
      this.props.addToCartCB(this.props.item)
    }

    getCost(quantity, price) {
      var cost = quantity * price
      return cost.toFixed(2)
    }

    getPrice(item) {
      if (item.onSale == "True") {
        var salePrice = (this.props.item.price * (1 - this.props.sale/100)).toFixed(2)
        return (
          <span>
            <h4>({this.props.sale}% off)</h4>
            <h3><span className="cross-out">${this.props.item.price}</span> <strong>${salePrice}</strong></h3>
          </span>
        )
        // <span className="sale-item">
        //   <h4>on sale!
        //   <svg height="21" viewBox="0 0 21 21" width="21" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd" stroke="#2a2e3b" stroke-linecap="round" stroke-linejoin="round" transform="matrix(-1 0 0 1 20 2)"><path d="m8.5 2.56534572h2c3.3137085 0 6 2.6862915 6 6v1.93465428c0 3.3137085-2.6862915 6-6 6h-2c-3.3137085 0-6-2.6862915-6-6v-1.93465428c0-3.3137085 2.6862915-6 6-6z"/><path d="m3.94265851-.12029102c-1.05323083.28505997-1.86575682 1.17688618-1.86575682 2.30840383 0 1.16606183.73081563 2.21070886 1.78973019 2.50733508" transform="matrix(.62932039 .77714596 -.77714596 .62932039 2.893856 -1.491094)"/><path d="m16.9295345-.10708618c-1.0898445.26224883-1.9419712 1.17003523-1.9419712 2.3284815 0 1.16644061.7312905 2.21138754 1.7907622 2.50762392" transform="matrix(-.62932039 .77714596 .77714596 .62932039 24.205765 -11.545558)"/><path d="m9.5 5.5v4h-3.5"/><path d="m15 15 2 2"/><path d="m2 15 2 2" transform="matrix(-1 0 0 1 6 0)"/></g></svg>
        //   </h4></span>
      }
    }
    // <h3>Cost: ${this.getCost(this.state.quantity, this.props.item.price)}</h3> <div className="flex-row align-center">

    render() {
       return (
          <div className="cart-item">
            <h2>
            <button className="remove-button" onClick={this.removeFromCart}>
              <svg height="30" viewBox="0 0 21 21" width="30" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd" stroke="#C64A38" stroke-linecap="round" stroke-linejoin="round" transform="translate(2 2)"><circle cx="8.5" cy="8.5" r="8"/><g transform="matrix(0 1 -1 0 17 0)"><path d="m5.5 11.5 6-6"/><path d="m5.5 5.5 6 6"/></g></g></svg>
            </button>
            {this.props.item.name}
            </h2>
            <div className="quantity-button-bar">
              <button onClick={this.removeOne}>&ndash;</button>
              <h3>{this.props.quantity} x ${this.getPrice(this.props.item)}</h3>
              <button onClick={this.addToCart}>+</button>
            </div>
          </div>
      );
    }

}

