import React, {Component} from 'react';

class Product extends Component {
    constructor(props) {
      super(props);
      this.state = {
          quantity: "No",
          fav: "All"
      };
    }


    render() {
       return (
           <div className="restaurant">
              <div className="nameAndFave">
                  <h3>Item</h3>
                  <button onClick={this.addToCart}>HI </button>
              </div>
          </div>
      );
    }

}

export default Product;


