const requestImageFile = require.context('./images', true, /.png$/);

class Product extends Component {
    constructor(props) {
      super(props);
    }

    addToCart = (event) => {
      this.props.addToCartCB(this.props.item);
    }

    getImage(imgName) {
      // var path=
      // console.log(path)
      return ("../../images/" + imgName).toString()
    }

    render() {
      // console.log("../../images/" + this.props.item.image)
      return (
         <div className="product">
            <img src={require("" + this.props.item.image)}></img>
            <h2>{this.props.item.name}</h2>
            <h3>Price: ${this.props.item.price}</h3>
            <button onClick={this.addToCart}>
