import React, { Component } from "react";
import "./App.css";
import Product from "./Product.js";


class ProductList extends Component {
    constructor(props) {
      super(props);
      // this.state = {
      //   items: []
      // };
    }

  renderList() {
    const items = this.props.items.map(item => {
        return <Product item={item}/>
    });
    return items;
  }

  render() {
    return (
      <div id="product-list">
          {this.renderList()}
      </div>
    );
  }
}

export default ProductList;


