import React, {Component} from 'react';

class Product extends Component {
    constructor(props) {
      super(props);
      this.state = {
          price: "No",
          fav: "All"
      };
    }

    onFav = () => {
        this.props.getItem(this.props.nam);
        console.log(this.props.getItem(this.props.nam));
    }

    handleFav = item => {
        if(this.state.isFavorited === "No"){
             this.setState({isFavorited: "Yes"});
             this.onFav();
             this.renderFave();
        } else {
             this.setState({isFavorited: "No"});
        }
    }

    isFav(){
        if(this.props.fav === "Favorites"){
            if(this.state.isFavorited === "Yes"){
                return true;
            } else {
                return false;
            }
        } else {
          return true;
        }
    }

    renderFave() {
        if(this.state.isFavorited === "Yes"){
          return (
            <div>
              <FaHeart id="favoritedIcon" />
            </div>
          )
        } else {
          return (
            <div>
              <FaRegHeart id="unfavoritedIcon" />
            </div>
          )
        }
    }

    render() {
       return (
           <div className="restaurant" id={this.props.nam.replace(".","").replace(" ","").substring(0,4)}>
              <div className="nameAndFave">
                  <h3>{this.props.nam}</h3>
                  <button onClick={this.handleFav}> {this.renderFave()} </button>
              </div>
          </div>
      );
    }

}

export default ListItem;
