import React, { Component } from "react";
import Product from "./Product.js";
import ShoppingCart from "./ShoppingCart.js";
import './style.css';


class HomePage extends Component {
    constructor(props) {
      super(props);
    }

    /** Renders the product list by calling renderProduct() on each item. */
    renderList() {
      const items = this.props.list.map(item => {
          return this.renderProduct(item)
      });
      return items;
    }

    /** Renders a product tile to be displayed in product list. */
    renderProduct(item) {
      return (<Product key={item.name}
                item={item}
                addToCartCB={this.addToCartCB.bind(this)}
                constants={this.props.constants}/>)
    }

    addToCart = (event) => {
      this.props.addToCartCB(this.props.item);
    }

    removeAll = (event) => {
      this.props.removeAllCB(this.props.item);
    }

    removeOne = (event) => {
      this.props.removeOneCB(this.props.item);
    }

    render() {
      return (
        <div className="app-container">
          {this.renderList()}
          <ShoppingCart
            removeAllCB={this.props.removeAll.bind(this)}
            addToCartCB={this.props.addToCart.bind(this)}
            removeOneCB={this.props.removeOne.bind(this)}/>
        </div>
      )
    }
}

export default HomePage;
