import React, { Component } from "react";
import Product from "./Product.js";
import ShoppingCart from "./ShoppingCart.js";
import CartItem from "./CartItem.js";
import './style.css';
import {
  Switch,
  Route,
  Link
} from "react-router-dom";


class ProductPage extends Component {
    constructor(props) {
      super(props);
    }

    /** Renders the product list by calling renderProduct() on each item. */
    renderList() {
      const items = this.props.list.map(item => {
          return this.renderProduct(item)
      });
      return items;
    }

    /** Renders a product tile to be displayed in product list. */
    renderProduct(item) {
      // redirectCB={this.renderProductPage.bind(this)}
      return (<Product key={item.name}
                item={item}
                addToCartCB={this.addToCart.bind(this)}
                constants={constants}/>)
    }

    render() {
      return (
        <ShoppingCart
          removeAllCB={this.props.removeAllCB.bind(this)}
          addToCartCB={this.props.addToCartCB.bind(this)}
          removeOneCB={this.props.removeOneCB.bind(this)}/>
      )
    }
}

export default ProductPage;
