import React, { Component } from "react";


class ProductPage extends Component {
    constructor(props) {
      super(props);
    }

    getItem(sku) {
      for (var i=0; i<this.props.items.length; i++) {
        if (this.props.items[i].sku == sku) {
          return this.props.items[i]
        }
      }
    }
    // <Product key={item.name}
