import React, {Component} from 'react';
import './style.css';

class CartItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
          quantity: 1
      };
      // this.addToCart = this.addToCart.bind(this)
    }

    removeOne = (event) => {
      if (this.props.quantity == 1) {
        this.removeFromCart()
      } else {
        // console.log("remove one")
        this.props.removeOneCB(this.props.item)
      }
    }

    removeFromCart = (event) => {
      // console.log("remove")
      this.props.removeAllCB(this.props.item)
    }

    addToCart = (event) => {
      // console.log("add")
      this.setState({ quantity: this.state.quantity + 1})
      this.props.addToCartCB(this.props.item)
    }

    render() {
       return (
           <div className="cart-item">
              <h2>{this.props.item.name}</h2>
              <div className="quantity-button-bar">
                <button onClick={this.removeOne}>-</button>
                <h3>{this.props.quantity} x ${this.props.item.price}</h3>
                <button onClick={this.addToCart}>+</button>
              </div>
              <h3>Cost: ${(this.state.quantity * this.props.item.price)}</h3>
              <button className="remove-button" onClick={this.removeFromCart}>
                <span className="button-content"><svg height="21" viewBox="0 0 21 21" width="21" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd" stroke="#2a2e3b" stroke-linecap="round" stroke-linejoin="round" transform="translate(5 5)"><path d="m.5 10.5 10-10"/><path d="m10.5 10.5-10-10z"/></g></svg> remove</span>
              </button>
          </div>
      );
    }

}

export default CartItem;
