import React, {Component} from 'react';
import './style.css';

class CartItem extends Component {
    constructor(props) {
      super(props);
      this.state = {
          quantity: 1
      };
    }

    // removeFromCart() {
    //   this.setState({quantity: 0});
    // }

    changeQuantity = (delta) => {
      this.setState((state) => {
        return {quantity: state.quantity + delta}
      });
    }

    removeFromCart = (event) => {
      console.log("remove")
      this.setState({quantity: 0})
      this.props.removeCB(this.props.item)
    }


    render() {
       return (
           <div className="cart-item">
              <h2>{this.props.item.name}</h2>
              <h3>Quantity: {this.state.quantity}</h3>
              <h3>Price: {this.props.item.price}</h3>
              <h3>Cost: {(this.state.quantity * this.props.item.price)}</h3>
              <button onClick={this.removeFromCart}>remove</button>
              <button onClick={() => this.setState({ quantity: this.state.quantity - 1})}>-</button>
              <button onClick={() => this.setState({ quantity: this.state.quantity + 1})}>+</button>
          </div>
      );
    }
    // <button onClick={this.changeQuantity(-1)}>-</button>
    // <button onClick={this.changeQuantity(1)}>+</button>

}

export default CartItem;
