const requestImageFile = require.context('./images', true, /.jpeg$/);

class Product extends Component {
    constructor(props) {
      super(props);
      // for (var item in this.props.items) {
      //   import(`./images/` + item.image)
      // }
    }

    addToCart = (event) => {
      this.props.addToCartCB(this.props.item);
    }

    loadImage = (imageName) => {
      import(`./images/${imageName}`).then(image => {
        return image
      });
    };

    // getImage(imgName) {
    //   // var path=
    //   // console.log(path)
    //   return ("../../images/" + imgName).toString()
    // }
    handleSale(onSale) {
      if (onSale) {
        return (<span className="sale-item"><h4>on sale!</h4></span>)
      } else {
        return ""
      }
    }

    render() {
      // console.log("../../images/" + this.props.item.image)
      // <img src={requestImageFile(`./${this.props.item.image}.jpeg`)}></img>
      // <img src={this.loadImage(this.props.item.image)}></img>
      return (
         <div className="product">
            <h2>{this.props.item.name}</h2>
            {this.handleSale()}
            <h3>Price: ${this.props.item.price}</h3>
